	      	isLoading: true,
	    })
	    this.fetchData(url)
	}

	render(){

		if(!this.state.isModeSelected){
	    	return (
	    		<div className="container">
	        		<ModeSelector onSelect={this.modeSelectHandler} />
	        	</div>
	      	)
	    }

		return (
			<div className="container">
				MyApp
			</div>
		);
	}
}

export default App;
