import React, {useState} from 'react'

export default props => {
	const [value, setValue] = useState("");
	const [disabled, setDisabled] = useState(true);

    const changeNewMessage = event => {
        setValue(event.target.value);
    	if (value == ""){
    		setDisabled(true);
    		return
    	}
    	setDisabled(false);
    }
    const changeEditMessage = event => {
        setValue(event.target.value);
    }

    debugger;
    //props.editMode == false ? setValue("") : setValue(props.editMessage.message);

	return(

        <div className="card-footer row m-0">
            <div className="col">
                {props.editMode == false ? <textarea className="form-control" onChange={changeNewMessage}></textarea> : <textarea className="form-control" onChange={changeEditMessage} value={props.editMessage.message}></textarea>}
            </div>
            <div className="col-3">
                {props.editMode == false ? <button type="button" className="btn btn-primary" onClick={() => props.newMessage(value)} disabled={disabled}>Отправить</button> : <button type="button" className="btn btn-primary" onClick={() => props.editMessageDone(value)}>Редактировать</button>}
            </div>
        </div>
	)
}
