import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import ModeSelector from './ModeSelector/ModeSelector';
import Chat from './Chat/Chat.js';
import MessageEditor from './MessageEditor/MessageEditor.js';

class App extends Component {

	state = {
		isModeSelected: false,
		data: [],
		users: [],
		primaryKey: 0,
		editMode: false,
		editMessage: ""
    	//isLoading: false
	}

	fetchData(url) {
	    const allData = require('./json/jsonMessages1.json');
	    const data = allData.messages;
	    const users = require('./json/jsonUsers.json');
	    this.setState({
	      	//isLoading: false
	      	data,
	      	users,
	      	primaryKey: allData.primaryKey
	    })
	}

	modeSelectHandler = url => {
	    // console.log(url)
	    this.setState({
	      	isModeSelected: true
	      	//isLoading: true
	    })
	    this.fetchData(url)
	}

	newMessage = message => {
	    console.log(message);
	    const newData = this.state.data;
	    let primaryKey = this.state.primaryKey
	    newData["message-" + primaryKey] = {"id": primaryKey, "userId": "1", "message": message, "date": "13:21"};
	    primaryKey++;
	    this.setState({
	    	data: newData,
	    	primaryKey: primaryKey
	    });
	    //console.log(this.state.data);
	    //console.log(this.state.primaryKey);
  	}

  	deleteMessage = item => {
  		const keyMessage = "message-" + item.id;
  		const newData = this.state.data;
  		delete newData[keyMessage];
  		this.setState({
  			data: newData
  		});
  		//console.log(newData);
  	}

  	editMessage = item => {
  		this.setState({
  			editMode: true,
  			editMessage: item
  		})
  	}

	render(){

		if(!this.state.isModeSelected){
	    	return (
	    		<div className="container">
	        		<ModeSelector onSelect={this.modeSelectHandler} />
	        	</div>
	      	)
	    }

		return (
			<div className="container">
				<div className="container mt-5">
					<div className="row d-flex flex-row-reverse">
						<div className="col-md-6 d-flex flex-row-reverse">
							<div className="card col-12 p-0">
								<div className="card-header px-5">
									<div className="row">
										<div className="col">img</div>
										<div className="col">user info</div>
										<div className="col">out</div>
									</div>
								</div>
								<div className="card-body">
									<Chat 
										data={this.state.data}
										users={this.state.users}
										del={this.deleteMessage}
									/>
								</div>
								<MessageEditor 
									newMessage={this.newMessage}
								/>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default App;
