import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import ModeSelector from './ModeSelector/ModeSelector';
import Chat from './Chat/Chat.js';
import MessageEditor from './MessageEditor/MessageEditor.js';

class App extends Component {

	state = {
		isModeSelected: false,
		data: [],
		users: [],
		primaryKey: ""
    	//isLoading: false
	}

	fetchData(url) {
	    const allData = require('./json/jsonMessages1.json');
	    const users = require('./json/jsonUsers.json');
	    console.log(allData);
	    this.setState({
	      	//isLoading: false
	      	data: allData,
	      	users
	    })
	}

	modeSelectHandler = url => {
	    // console.log(url)
	    this.setState({
	      	isModeSelected: true
	      	//isLoading: true
	    })
	    this.fetchData(url)
	}

	newMessage = message =>{
	    console.log(message);
	    /*const newData = this.state.data.concat();
	    newData.unshift(addEl);
	    this.setState({data: newData})

	    this.state.data.map(item => (console.log(item.firstName)));

	    console.log(this.state.data);*/
  	}

	render(){

		if(!this.state.isModeSelected){
	    	return (
	    		<div className="container">
	        		<ModeSelector onSelect={this.modeSelectHandler} />
	        	</div>
	      	)
	    }

		return (
			<div className="container">
				<div className="container mt-5">
					<div className="row d-flex flex-row-reverse">
						<div className="col-md-6 d-flex flex-row-reverse">
							<div className="card col-12 p-0">
								<div className="card-header px-5">
									<div className="row">
										<div className="col">img</div>
										<div className="col">user info</div>
										<div className="col">out</div>
									</div>
								</div>
								<div className="card-body">
									<Chat 
										data={this.state.data}
										users={this.state.users}
									/>
								</div>
								<MessageEditor 
									newMessage={this.newMessage}
								/>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default App;
