import React, {useState} from 'react';

export default props => {

	const [value, setValue] = useState({login: "", password: ""});

	const changeHandler = (param) => (event) => {
    	value[param] = event.target.value;
        setValue(value);
    }



	return(
		<div className="col-3">
			<div className="form-group">
			  	<label for="exampleInputEmail1">Email address</label>
			  	<input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter login" onChange={changeHandler("login")}/>
			  	<small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
			</div>
			<div className="form-group">
			  	<label for="exampleInputPassword1">Password</label>
		   		<input type="password" className="form-control" id="exampleInputPassword1" placeholder="Password" onChange={changeHandler("password")}/>
			</div>
			<button type="submit" className="btn btn-primary" onClick={() => props.logIn(value)}>Войти</button>
		</div>
	)
}
