import React from 'react';

export default props => {
	/*props.data.map(item => {
		debugger;
		const userKey = "user-" + item.userId;
		const userName = props.users[userKey].logIn;
		return(

			
				<div className="row my-2" key={item.id}>
					<div className="col-2 p-0 ml-3">
						img
					</div>
					<div className="col bg-secondary text-white mr-3 rounded">
						<div className="d-flex flex-column">
							<div className="row m-0">
								<div className="col p-0">
									{ userName }
								</div>
								<div className="col-3">
														
								</div>
							</div>
							<div>
								{ item.message }
							</div>
							<div>
								<span className="float-right">{ item.date }</span>
							</div>
						</div>
					</div>
				</div>
		)
	})*/
	const items = props.data;
	for (let key in props.data ){
		const userKey = "user-" + item.userId;
		const userName = props.users[userKey].logIn;
		return(

			
				<div className="row my-2" key={item.id}>
					<div className="col-2 p-0 ml-3">
						img
					</div>
					<div className="col bg-secondary text-white mr-3 rounded">
						<div className="d-flex flex-column">
							<div className="row m-0">
								<div className="col p-0">
									{ userName }
								</div>
								<div className="col-3">
														
								</div>
							</div>
							<div>
								{ item.message }
							</div>
							<div>
								<span className="float-right">{ item.date }</span>
							</div>
						</div>
					</div>
				</div>
		)
	}
}
