import React from 'react';

export default props => {
	const dataArr = Object.values(props.data);
	return(
		dataArr.map(item => {
			const userKey = "user-" + item.userId;
			const userName = props.users[userKey].logIn;
			return(

				
					<div className="row my-2" key={item.id}>
						<div className="col-2 p-0 ml-3">
							img
						</div>
						<div className={"col text-white mr-3 rounded" + item.userId == props.currentUserId ? "bg-primary" : "bg-secondary"} >
							<div className="d-flex flex-column">
								<div className="row m-0">
									<div className="col p-0">
										{ userName }
									</div>
									<div className="col-3">
										<span onClick={() => props.del(item)}>уд</span>
										<span> | </span>
										<span onClick={() => props.edit(item)}>ред</span>
									</div>
								</div>
								<div>
									{ item.message }
								</div>
								<div>
									<span className="float-right">{ item.date }</span>
								</div>
							</div>
						</div>
					</div>
			)
		})
