import React, {useState} from 'react'

export default props => {
	const [value, setValue] = useState("");
	const [disabled, setDisabled] = useState(true);

    const activateButton = event => {
        setValue(event.target.value);
    	if (value == ""){
    		setDisabled(true);
    		return
    	}
    	setDisabled(false);
    }

	return(

        <div className="card-footer row m-0">
            <div className="col">
                {props.editMode == false ? <textarea className="form-control" onChange={activateButton}></textarea> : <textarea className="form-control">editMessage.message</textarea>}
                }
            </div>
            <div className="col-3">
                <button type="button" className="btn btn-primary" onClick={() => props.newMessage(value)} disabled={disabled}>Отправить</button>
            </div>
        </div>
	)
}
